mapper.select:
select * from (
select row_number()over(order by id) row_no,

from hr_employee
where 1=1

  and employee_no = #{employNo,jdbcType=VARCHAR}


  and name like '%${name}%'


  and id_card = #{idCard,jdbcType=VARCHAR}


  and month(birthday) = #{birthdayMonth,jdbcType=INTEGER}


  and contract_end_date <= #{contractDate,jdbcType=TIMESTAMP}


  and state = #{state,jdbcType=TINYINT}


) a where row_no between ${start} and ${end}

select count(*)
from hr_employee
where 1=1

  and employee_no = #{employNo,jdbcType=VARCHAR}


  and name like '%${name}%'


  and id_card = #{idCard,jdbcType=VARCHAR}


  and month(birthday) = #{birthdayMonth,jdbcType=INTEGER}


  and contract_end_date <= #{contractDate,jdbcType=TIMESTAMP}


  and state = #{state,jdbcType=TINYINT}

select

from hr_employee
where 1=1

  and month(birthday) = #{birthdayMonth,jdbcType=INTEGER}


  and datediff(dd,join_date,getdate())>= #{noContractDay,jdbcType=INTEGER}
  and contract_start_date is null


  and contract_end_date <= #{contractDate,jdbcType=TIMESTAMP}


  and state = #{state,jdbcType=TINYINT}
