mapper.select:
select

from rad_product
where industry = #{industry,jdbcType=INTEGER}
and model = #{model,jdbcType=VARCHAR}

select * from (
select row_number()over(order by id) row_no,
from rad_product
where 1=1

  and type_id = #{typeId,jdbcType=INTEGER}


  and industry = #{industry,jdbcType=INTEGER}


  and model = #{model,jdbcType=VARCHAR}

) a where row_no between ${start} and ${end}

select count(*) from rad_product
where 1=1

  and type_id = #{typeId,jdbcType=INTEGER}


  and industry = #{industry,jdbcType=INTEGER}


  and model = #{model,jdbcType=VARCHAR}

select distinct a.* from rad_product a


    select product_id from rad_product_detail where config_value <3 and config_id = #{id}


where 1=1

  and a.id=b.product_id


  and a.industry = #{industryId,jdbcType=INTEGER}


  and a.model = #{model,jdbcType=VARCHAR}

mapper.insert:
insert into rad_product (type_id, model,
  industry, model_desc, remark, 
  create_id, create_name, create_date, 
  modify_id, modify_name, modify_date
  )
values (#{typeId,jdbcType=INTEGER}, #{model,jdbcType=VARCHAR},
  #{industry,jdbcType=INTEGER}, #{modelDesc,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, 
  #{createId,jdbcType=BIGINT}, #{createName,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}, 
  #{modifyId,jdbcType=BIGINT}, #{modifyName,jdbcType=VARCHAR}, #{modifyDate,jdbcType=TIMESTAMP}
  )
