mapper.select:
select

from rad_wordbook
where type = #{type,jdbcType=TINYINT}
and name = #{name,jdbcType=VARCHAR}

select * from (
select row_number()over(order by id) row_no,

from rad_wordbook
where 1=1

  and type = #{type,jdbcType=TINYINT}


  and name like '%${name}%'

) a where row_no between ${start} and ${end}

select count(*) from rad_wordbook where 1=1
and type = #{type,jdbcType=TINYINT}
and name like '%${name}%'

update.foreach:
update rad_wordbook
set seq = #{item.seq,jdbcType=INTEGER}
where id = #{item.id,jdbcType=INTEGER}
